	private static boolean disjoint(Set<String> leftBindings, Set<String> rightBindings) {
		if (leftBindings.isEmpty() || rightBindings.isEmpty()) {
			return true;
		}
		for (String name : leftBindings) {
			if (rightBindings.contains(name)) {
				return false;
			}
		}
		return true;
	}
