package org.eclipse.rdf4j.query.algebra.evaluation.optimizer;

import java.util.ArrayDeque;
import java.util.ArrayList;
import java.util.Deque;
import java.util.List;

import org.eclipse.rdf4j.query.BindingSet;
import org.eclipse.rdf4j.query.Dataset;
import org.eclipse.rdf4j.query.algebra.BinaryTupleOperator;
import org.eclipse.rdf4j.query.algebra.QueryModelNode;
import org.eclipse.rdf4j.query.algebra.TupleExpr;
import org.eclipse.rdf4j.query.algebra.UnaryTupleOperator;
import org.eclipse.rdf4j.query.algebra.Union;
import org.eclipse.rdf4j.query.algebra.evaluation.QueryOptimizer;
import org.eclipse.rdf4j.query.algebra.helpers.AbstractSimpleQueryModelVisitor;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

/**
 * Flatten nested UNION nodes into a right-deep chain while preserving scope change flags.
 */
public class UnionFlatteningOptimizer implements QueryOptimizer {

	private static final String DUMP_PLANS_PROPERTY = "rdf4j.optimizer.unionOptional.dumpPlans";
	private static final Logger LOGGER = LoggerFactory.getLogger(UnionFlatteningOptimizer.class);

	@Override
	public void optimize(TupleExpr tupleExpr, Dataset dataset, BindingSet bindings) {
		tupleExpr.visit(new UnionFlattener());
	}

	private static final class UnionFlattener extends AbstractSimpleQueryModelVisitor<RuntimeException> {

		private UnionFlattener() {
			super(true);
		}

		@Override
		public void meet(Union union) {
			super.meet(union);

			boolean scopeChange = union.isVariableScopeChange();
			List<TupleExpr> args = new ArrayList<>();
			collectArgs(union, scopeChange, args);
			if (args.size() <= 2) {
				return;
			}

			logFlatten(union, args.size(), scopeChange);
			Union replacement = buildRightDeep(args, scopeChange);
			if (union.getParentNode() != null) {
				union.replaceWith(replacement);
			} else {
				union.setLeftArg(replacement.getLeftArg());
				union.setRightArg(replacement.getRightArg());
				union.setVariableScopeChange(scopeChange);
			}
		}

		private static void collectArgs(Union union, boolean scopeChange, List<TupleExpr> args) {
			TupleExpr left = union.getLeftArg();
			TupleExpr right = union.getRightArg();

			if (left instanceof Union && ((Union) left).isVariableScopeChange() == scopeChange) {
				collectArgs((Union) left, scopeChange, args);
			} else {
				args.add(left);
			}

			if (right instanceof Union && ((Union) right).isVariableScopeChange() == scopeChange) {
				collectArgs((Union) right, scopeChange, args);
			} else {
				args.add(right);
			}
		}

		private static Union buildRightDeep(List<TupleExpr> args, boolean scopeChange) {
			int last = args.size() - 1;
			Union current = new Union(args.get(last - 1), args.get(last));
			current.setVariableScopeChange(scopeChange);
			for (int i = last - 2; i >= 0; i--) {
				Union next = new Union(args.get(i), current);
				next.setVariableScopeChange(scopeChange);
				current = next;
			}
			return current;
		}
	}

	private static void logFlatten(Union union, int argCount, boolean scopeChange) {
		if (!isDumpPlansEnabled() || !LOGGER.isDebugEnabled()) {
			return;
		}
		LOGGER.debug("UnionFlatteningOptimizer applied at {} args={} scopeChange={}",
				nodePath(union), argCount, scopeChange);
	}

	private static boolean isDumpPlansEnabled() {
		return Boolean.parseBoolean(System.getProperty(DUMP_PLANS_PROPERTY, "false"));
	}

	private static String nodePath(QueryModelNode node) {
		Deque<String> segments = new ArrayDeque<>();
		QueryModelNode current = node;
		while (current != null) {
			QueryModelNode parent = current.getParentNode();
			String label = current.getClass().getSimpleName();
			if (parent == null) {
				segments.addFirst(label);
				break;
			}
			int index = childIndex(parent, current);
			segments.addFirst(label + "[" + (index >= 0 ? index : "?") + "]");
			current = parent;
		}
		segments.addFirst("root");
		return String.join("/", segments);
	}

	private static int childIndex(QueryModelNode parent, QueryModelNode child) {
		if (parent instanceof UnaryTupleOperator) {
			return 0;
		}
		if (parent instanceof BinaryTupleOperator) {
			BinaryTupleOperator binary = (BinaryTupleOperator) parent;
			if (binary.getLeftArg() == child) {
				return 0;
			}
			if (binary.getRightArg() == child) {
				return 1;
			}
		}
		return -1;
	}
}
