/**
 * Reorder UNION arguments using conservative cardinality estimates.
 */
public class UnionReorderOptimizer implements QueryOptimizer {

	private static final String MIN_RATIO_PROPERTY = "rdf4j.optimizer.unionOptional.unionReorder.minRatio";
	private static final String MIN_WORK_PROPERTY = "rdf4j.optimizer.unionOptional.unionReorder.minWork";
	private static final String DUMP_PLANS_PROPERTY = "rdf4j.optimizer.unionOptional.dumpPlans";
	private static final double DEFAULT_MIN_RATIO = 1.5;
	private static final double DEFAULT_MIN_WORK = 1000.0;
	private static final Logger LOGGER = LoggerFactory.getLogger(UnionReorderOptimizer.class);

	private final EvaluationStatistics statistics;
	private final CardinalityEstimator estimator;
	private final double minRatio;
	private final double minWork;

	public UnionReorderOptimizer(EvaluationStatistics statistics) {
		this.statistics = statistics;
		this.estimator = new CardinalityEstimator(statistics);
		this.minRatio = readMinRatio();
		this.minWork = readMinWork();
	}

	@Override
	public void optimize(TupleExpr tupleExpr, Dataset dataset, BindingSet bindings) {
		tupleExpr.visit(new UnionReorderer());
	}

	private final class UnionReorderer extends AbstractSimpleQueryModelVisitor<RuntimeException> {
		private UnionReorderer() {
			super(true);
		}

		@Override
		public void meet(Union union) {
			super.meet(union);

			TupleExpr leftArg = union.getLeftArg();
			TupleExpr rightArg = union.getRightArg();

			CardinalityEstimator.Estimate leftEstimate = estimator.estimate(leftArg);
			CardinalityEstimator.Estimate rightEstimate = estimator.estimate(rightArg);
			double leftWork = leftEstimate.getWork();
			double rightWork = rightEstimate.getWork();

			if (leftWork <= 0 || rightWork <= 0) {
				return;
			}
			if (Math.max(leftWork, rightWork) < minWork) {
				return;
			}

			double ratio = Math.max(leftWork, rightWork) / Math.min(leftWork, rightWork);
			if (ratio < minRatio) {
				return;
			}

			if (rightWork < leftWork) {
				logReorder(union, leftEstimate, rightEstimate, ratio, minRatio);
				union.setLeftArg(rightArg);
				union.setRightArg(leftArg);
			}
		}
	}

	private static void logReorder(Union union, CardinalityEstimator.Estimate leftEstimate,
			CardinalityEstimator.Estimate rightEstimate, double ratio, double minRatio) {
		if (!isDumpPlansEnabled() || !LOGGER.isDebugEnabled()) {
			return;
		}
		LOGGER.debug(
				"UnionReorderOptimizer applied at {} leftRows={} rightRows={} leftWork={} rightWork={} ratio={} minRatio={}",
				nodePath(union),
				leftEstimate.getRows(),
				rightEstimate.getRows(),
				leftEstimate.getWork(),
				rightEstimate.getWork(),
				ratio,
				minRatio);
	}

	private static double readMinRatio() {
		String raw = System.getProperty(MIN_RATIO_PROPERTY);
		if (raw == null || raw.isBlank()) {
			return DEFAULT_MIN_RATIO;
		}
		try {
			double parsed = Double.parseDouble(raw.trim());
			return parsed <= 1.0 ? DEFAULT_MIN_RATIO : parsed;
		} catch (NumberFormatException ignore) {
			return DEFAULT_MIN_RATIO;
		}
	}

	private static double readMinWork() {
		String raw = System.getProperty(MIN_WORK_PROPERTY);
		if (raw == null || raw.isBlank()) {
			return DEFAULT_MIN_WORK;
		}
		try {
			double parsed = Double.parseDouble(raw.trim());
			return parsed <= 0.0 ? DEFAULT_MIN_WORK : parsed;
		} catch (NumberFormatException ignore) {
			return DEFAULT_MIN_WORK;
		}
	}

	private static boolean isDumpPlansEnabled() {
		return Boolean.parseBoolean(System.getProperty(DUMP_PLANS_PROPERTY, "false"));
	}

	private static String nodePath(QueryModelNode node) {
		Deque<String> segments = new ArrayDeque<>();
		QueryModelNode current = node;
		while (current != null) {
			QueryModelNode parent = current.getParentNode();
			String label = current.getClass().getSimpleName();
			if (parent == null) {
				segments.addFirst(label);
				break;
			}
			int index = childIndex(parent, current);
			segments.addFirst(label + "[" + (index >= 0 ? index : "?") + "]");
			current = parent;
		}
		segments.addFirst("root");
		return String.join("/", segments);
	}

	private static int childIndex(QueryModelNode parent, QueryModelNode child) {
		if (parent instanceof UnaryTupleOperator) {
			return 0;
		}
		if (parent instanceof BinaryTupleOperator) {
			BinaryTupleOperator binary = (BinaryTupleOperator) parent;
			if (binary.getLeftArg() == child) {
				return 0;
			}
			if (binary.getRightArg() == child) {
				return 1;
			}
		}
		return -1;
	}
}
