package org.eclipse.rdf4j.query.algebra.evaluation.optimizer.learned;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertNotNull;

import java.util.ArrayDeque;
import java.util.ArrayList;
import java.util.Deque;
import java.util.List;

import org.eclipse.rdf4j.model.IRI;
import org.eclipse.rdf4j.model.ValueFactory;
import org.eclipse.rdf4j.model.impl.SimpleValueFactory;
import org.eclipse.rdf4j.query.algebra.Exists;
import org.eclipse.rdf4j.query.algebra.Join;
import org.eclipse.rdf4j.query.algebra.StatementPattern;
import org.eclipse.rdf4j.query.algebra.TupleExpr;
import org.eclipse.rdf4j.query.algebra.Var;
import org.eclipse.rdf4j.query.algebra.evaluation.impl.EmptyTripleSource;
import org.eclipse.rdf4j.query.algebra.evaluation.impl.EvaluationStatistics;
import org.eclipse.rdf4j.query.algebra.evaluation.optimizer.JoinStatsProvider;
import org.eclipse.rdf4j.query.algebra.evaluation.optimizer.LearnedQueryJoinOptimizer;
import org.eclipse.rdf4j.query.algebra.evaluation.optimizer.MemoryJoinStats;
import org.eclipse.rdf4j.query.algebra.helpers.AbstractQueryModelVisitor;
import org.eclipse.rdf4j.query.algebra.helpers.collectors.StatementPatternCollector;
import org.eclipse.rdf4j.query.impl.EmptyBindingSet;
import org.eclipse.rdf4j.query.parser.ParsedQuery;
import org.eclipse.rdf4j.query.parser.sparql.SPARQLParser;
import org.junit.jupiter.api.Test;

class LearnedQueryJoinOptimizerExistsBoundVarsTest {

	private static final ValueFactory VF = SimpleValueFactory.getInstance();
	private static final String EX_NS = "http://example.com/";
	private static final IRI HAS_MEDICATION = VF.createIRI(EX_NS, "hasMedication");
	private static final IRI CODE = VF.createIRI(EX_NS, "code");

	private static final String QUERY = String.join("\n",
			"PREFIX ex: <http://example.com/>",
			"SELECT * WHERE {",
			"  ?patient ex:hasMedication ?med .",
			"  FILTER(?med != ?patient && NOT EXISTS {",
			"    ?patient ex:hasMedication ?m2 .",
			"    ?m2 ex:code ?c .",
			"    FILTER(?c = \"MED-1005\")",
			"  })",
			"}");

	@Test
	void treatsCorrelatedVarsAsBoundInsideExists() throws Exception {
		EvaluationStatistics stats = new ExistsBoundEvaluationStatistics();
		JoinStatsProvider statsProvider = new MemoryJoinStats(MemoryJoinStats.InvalidationSettings.disabled());
		LearnedJoinConfig config = new LearnedJoinConfig(LearnedJoinConfig.DEFAULT_DP_THRESHOLD, true);
		LearnedQueryJoinOptimizer optimizer = new LearnedQueryJoinOptimizer(stats, new EmptyTripleSource(),
				statsProvider, config);

		ParsedQuery parsed = new SPARQLParser().parseQuery(QUERY, null);
		TupleExpr expr = parsed.getTupleExpr();
		optimizer.optimize(expr, null, EmptyBindingSet.getInstance());

		Exists exists = findExists(expr);
		assertNotNull(exists, "Expected EXISTS subquery");

		List<String> order = joinOrderSignature(exists.getSubQuery());
		assertEquals(List.of(HAS_MEDICATION.stringValue(), CODE.stringValue()), order);
	}

	private static Exists findExists(TupleExpr expr) {
		Exists[] found = new Exists[1];
		expr.visit(new AbstractQueryModelVisitor<RuntimeException>() {
			@Override
			public void meet(Exists node) {
				if (found[0] == null) {
					found[0] = node;
				}
				super.meet(node);
			}
		});
		return found[0];
	}

	private static List<String> joinOrderSignature(TupleExpr expr) {
		Join join = findLargestJoin(expr);
		if (join == null) {
			return List.of();
		}
		List<TupleExpr> operands = flattenJoin(join);
		List<String> order = new ArrayList<>(operands.size());
		for (TupleExpr operand : operands) {
			List<StatementPattern> patterns = StatementPatternCollector.process(operand);
			if (patterns.isEmpty()) {
				order.add(operand.getClass().getSimpleName());
				continue;
			}
			Var predicate = patterns.get(0).getPredicateVar();
			order.add(predicateLabel(predicate));
		}
		return order;
	}

	private static Join findLargestJoin(TupleExpr expr) {
		Join[] best = new Join[1];
		int[] bestSize = new int[1];
		expr.visit(new AbstractQueryModelVisitor<RuntimeException>() {
			@Override
			public void meet(Join node) {
				int size = flattenJoin(node).size();
				if (size > bestSize[0]) {
					bestSize[0] = size;
					best[0] = node;
				}
				super.meet(node);
			}
		});
		return best[0];
	}

	private static List<TupleExpr> flattenJoin(TupleExpr expr) {
		List<TupleExpr> operands = new ArrayList<>();
		Deque<TupleExpr> stack = new ArrayDeque<>();
		stack.push(expr);
		while (!stack.isEmpty()) {
			TupleExpr current = stack.pop();
			if (current instanceof Join) {
				Join join = (Join) current;
				stack.push(join.getRightArg());
				stack.push(join.getLeftArg());
			} else {
				operands.add(current);
			}
		}
		return operands;
	}

	private static String predicateLabel(Var predicate) {
		if (predicate == null || !predicate.hasValue()) {
			return "<var>";
		}
		return predicate.getValue().stringValue();
	}

	private static final class ExistsBoundEvaluationStatistics extends EvaluationStatistics {
		@Override
		public double getCardinality(TupleExpr expr) {
			if (expr instanceof StatementPattern) {
				StatementPattern pattern = (StatementPattern) expr;
				IRI predicate = predicate(pattern);
				if (predicate == null) {
					return super.getCardinality(expr);
				}
				if (HAS_MEDICATION.equals(predicate)) {
					return isBound(pattern.getSubjectVar()) ? 100.0d : 1000.0d;
				}
				if (CODE.equals(predicate)) {
					return isBound(pattern.getObjectVar()) ? 10.0d : 200.0d;
				}
			}
			return super.getCardinality(expr);
		}

		private IRI predicate(StatementPattern pattern) {
			if (pattern.getPredicateVar() == null || !pattern.getPredicateVar().hasValue()
					|| !(pattern.getPredicateVar().getValue() instanceof IRI)) {
				return null;
			}
			return (IRI) pattern.getPredicateVar().getValue();
		}

		private boolean isBound(Var var) {
			return var != null && var.hasValue();
		}
	}
}
