package org.eclipse.rdf4j.sail.elasticsearchstore;

import static org.junit.jupiter.api.Assertions.assertNotNull;
import static org.junit.jupiter.api.Assertions.assertThrows;

import org.eclipse.rdf4j.sail.SailException;
import org.junit.jupiter.api.Test;

public class SingletonClientProviderIT extends AbstractElasticsearchStoreIT {

	@Test
	public void getClientFailsIfClusterNameMismatches() {
		SingletonClientProvider provider = new SingletonClientProvider(elasticsearchHost(), elasticsearchPort(),
				elasticsearchCluster() + "-wrong");

		try {
			assertThrows(SailException.class, provider::getClient);
		} finally {
			provider.close();
		}
	}

	@Test
	public void getClientSucceedsIfClusterNameMatches() {
		SingletonClientProvider provider = new SingletonClientProvider(elasticsearchHost(), elasticsearchPort(),
				elasticsearchCluster());

		try {
			assertNotNull(provider.getClient());
		} finally {
			provider.close();
		}
	}
}
