package org.eclipse.rdf4j.sail.memory;

import java.io.IOException;

import org.eclipse.rdf4j.benchmark.common.ThemeQueryCatalog;
import org.eclipse.rdf4j.benchmark.rio.util.ThemeDataSetGenerator;
import org.eclipse.rdf4j.benchmark.rio.util.ThemeDataSetGenerator.Theme;
import org.eclipse.rdf4j.common.transaction.IsolationLevels;
import org.eclipse.rdf4j.query.Dataset;
import org.eclipse.rdf4j.query.algebra.evaluation.EvaluationStrategy;
import org.eclipse.rdf4j.query.algebra.evaluation.EvaluationStrategyFactory;
import org.eclipse.rdf4j.query.algebra.evaluation.TripleSource;
import org.eclipse.rdf4j.query.algebra.evaluation.impl.DefaultEvaluationStrategyFactory;
import org.eclipse.rdf4j.query.algebra.evaluation.impl.EvaluationStatistics;
import org.eclipse.rdf4j.query.algebra.evaluation.optimizer.StandardQueryOptimizerPipeline;
import org.eclipse.rdf4j.query.explanation.Explanation;
import org.eclipse.rdf4j.repository.sail.SailRepository;
import org.eclipse.rdf4j.repository.sail.SailRepositoryConnection;
import org.eclipse.rdf4j.repository.util.RDFInserter;
import org.junit.jupiter.api.Test;

class ThemeQueryPlanComparisonTest {

	private static final Theme THEME = Theme.SOCIAL_MEDIA;
	private static final int QUERY_INDEX = 5;

	@Test
	void logExecutedPlansWithAndWithoutSparqlUo() throws IOException {
		String query = ThemeQueryCatalog.queryFor(THEME, QUERY_INDEX);
		MemoryStore store = new MemoryStore();
		SailRepository repository = new SailRepository(store);
		repository.init();

		try {
			loadData(repository);
			logExecutedPlan(repository, query, "SparqlUo enabled (default)");

			store.setEvaluationStrategyFactory(createStandardPipelineFactory(store));
			logExecutedPlan(repository, query, "SparqlUo disabled (StandardQueryOptimizerPipeline)");
		} finally {
			repository.shutDown();
		}
	}

	private static void loadData(SailRepository repository) throws IOException {
		try (SailRepositoryConnection connection = repository.getConnection()) {
			connection.begin(IsolationLevels.NONE);
			RDFInserter inserter = new RDFInserter(connection);
			ThemeDataSetGenerator.generate(THEME, inserter);
			connection.commit();
		}
	}

	private static void logExecutedPlan(SailRepository repository, String query, String label) {
		try (SailRepositoryConnection connection = repository.getConnection()) {
			String explanation = connection.prepareTupleQuery(query)
					.explain(Explanation.Level.Executed)
					.toString();
			System.out.println("---- " + label + " ----");
			System.out.println(explanation);
		}
	}

	private static EvaluationStrategyFactory createStandardPipelineFactory(MemoryStore store) {
		DefaultEvaluationStrategyFactory factory = new DefaultEvaluationStrategyFactory(
				store.getFederatedServiceResolver()) {
			@Override
			public EvaluationStrategy createEvaluationStrategy(Dataset dataset, TripleSource tripleSource,
					EvaluationStatistics evaluationStatistics) {
				EvaluationStrategy strategy = super.createEvaluationStrategy(dataset, tripleSource,
						evaluationStatistics);
				strategy.setOptimizerPipeline(
						new StandardQueryOptimizerPipeline(strategy, tripleSource, evaluationStatistics));
				return strategy;
			}
		};
		factory.setQuerySolutionCacheThreshold(store.getIterationCacheSyncThreshold());
		factory.setTrackResultSize(store.isTrackResultSize());
		return factory;
	}
}
