import org.eclipse.rdf4j.repository.sail.SailRepositoryConnection;
import org.eclipse.rdf4j.repository.util.RDFInserter;
import org.eclipse.rdf4j.sail.memory.MemoryStore;

public final class ThemeQueryExplain {

	private ThemeQueryExplain() {
	}

	public static void main(String[] args) throws Exception {
		if (args == null || args.length < 4) {
			System.err.println("Usage: ThemeQueryExplain <THEME> <QUERY_INDEX> <sparqluo|standard> <optimized|executed>");
			System.exit(2);
			return;
		}

		Theme theme = Theme.valueOf(args[0]);
		int queryIndex = Integer.parseInt(args[1]);
		boolean useSparqlUo = switch (args[2].toLowerCase()) {
		case "sparqluo" -> true;
		case "standard" -> false;
		default -> throw new IllegalArgumentException("Expected sparqluo|standard, got: " + args[2]);
		};
		Explanation.Level level = switch (args[3].toLowerCase()) {
		case "optimized" -> Explanation.Level.Optimized;
		case "executed" -> Explanation.Level.Executed;
		default -> throw new IllegalArgumentException("Expected optimized|executed, got: " + args[3]);
		};

		SailRepository repository = createRepository(useSparqlUo);
		try {
			loadData(repository, theme);
			String query = ThemeQueryCatalog.queryFor(theme, queryIndex);
			System.out.println("Theme: " + theme);
			System.out.println("QueryIndex: " + queryIndex);
			System.out.println("UseSparqlUo: " + useSparqlUo);
			System.out.println("ExplainLevel: " + level);
			System.out.println("Query:\n" + query);
			System.out.println("Explanation:\n" + explain(repository, query, level));
		} finally {
			repository.shutDown();
		}
	}

	private static String explain(SailRepository repository, String query, Explanation.Level level) {
		try (SailRepositoryConnection connection = repository.getConnection()) {
			return connection.prepareTupleQuery(query).explain(level).toString();
		}
	}

	private static void loadData(SailRepository repository, Theme theme) throws Exception {
		try (SailRepositoryConnection connection = repository.getConnection()) {
			connection.begin(IsolationLevels.NONE);
			RDFInserter inserter = new RDFInserter(connection);
			ThemeDataSetGenerator.generate(theme, inserter);
			connection.commit();
		}
	}

	private static SailRepository createRepository(boolean useSparqlUo) {
		MemoryStore store = new MemoryStore();
		if (!useSparqlUo) {
			store.setEvaluationStrategyFactory(createStandardPipelineFactory(store));
		}
		return new SailRepository(store);
	}

	private static EvaluationStrategyFactory createStandardPipelineFactory(MemoryStore store) {
		DefaultEvaluationStrategyFactory factory = new DefaultEvaluationStrategyFactory(
				store.getFederatedServiceResolver()) {
			@Override
			public EvaluationStrategy createEvaluationStrategy(Dataset dataset, TripleSource tripleSource,
					EvaluationStatistics evaluationStatistics) {
				EvaluationStrategy strategy = super.createEvaluationStrategy(dataset, tripleSource,
						evaluationStatistics);
				strategy.setOptimizerPipeline(
						new StandardQueryOptimizerPipeline(strategy, tripleSource, evaluationStatistics));
				return strategy;
			}
		};
		factory.setQuerySolutionCacheThreshold(store.getIterationCacheSyncThreshold());
		factory.setTrackResultSize(store.isTrackResultSize());
		return factory;
	}
}
