package org.eclipse.rdf4j.tools.serverboot;

import java.io.IOException;
import java.util.Optional;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.web.filter.OncePerRequestFilter;

import jakarta.servlet.FilterChain;
import jakarta.servlet.ServletException;
import jakarta.servlet.http.HttpServletRequest;
import jakarta.servlet.http.HttpServletResponse;

class ErrorLoggingFilter extends OncePerRequestFilter {

	private static final Logger logger = LoggerFactory.getLogger(ErrorLoggingFilter.class);

	@Override
	protected void doFilterInternal(HttpServletRequest request, HttpServletResponse response, FilterChain filterChain)
			throws ServletException, IOException {
		boolean logged = false;
		int status = HttpServletResponse.SC_OK;
		try {
			filterChain.doFilter(request, response);
			status = response.getStatus();
		} catch (Exception ex) {
			logged = true;
			logEvent(request, response.getStatus(), ex);
			throw ex;
		} finally {
			if (!logged) {
				logEvent(request, status, null);
			}
		}
	}

	private void logEvent(HttpServletRequest request, int status, Exception error) {
		if (error == null && status < HttpServletResponse.SC_BAD_REQUEST) {
			return;
		}
		StringBuilder target = new StringBuilder(request.getMethod()).append(' ').append(request.getRequestURI());
		Optional.ofNullable(request.getQueryString()).ifPresent(query -> target.append('?').append(query));
		target.append(" from ").append(request.getRemoteAddr());
		Optional.ofNullable(request.getHeader("User-Agent"))
				.ifPresent(agent -> target.append(" UA=\"").append(agent).append('"'));

		if (error != null || status >= HttpServletResponse.SC_INTERNAL_SERVER_ERROR) {
			logger.error("HTTP {} {}", status, target, error);
		} else {
			logger.warn("HTTP {} {}", status, target);
		}
	}
}
